const poetryWall = document.getElementById("poetryWall");

let i = 0;
poets.forEach((poet) => {
  const card = document.createElement("div");
  card.classList.add("col-lg-3", "col-md-4", "col-sm-6", "mb-4");

  const img = document.createElement("img");
  img.classList.add("card-img-top");
  img.alt = poet.name;
  img.src = poet.img;

  const cardBody = document.createElement("div");
  cardBody.classList.add("card", "h-100", "poet-card");

  const cardContent = document.createElement("div");
  cardContent.classList.add("card-body");

  const poetName = document.createElement("h5");
  poetName.classList.add("text-center");
  poetName.textContent = poet.name;

  const poetBio = document.createElement("p");
  poetBio.classList.add("card-text");
  poetBio.id = "cardText" + i
  poetBio.innerHTML = poet.bio;

  const button = document.createElement("button");
  button.classList.add("btn", "btn-primary", "m-auto", "text-center");
  button.id = "showMoreButton" + i
  button.onclick = showMore.bind(null, i);
  button.textContent = "Tovább";
  i++;

  cardContent.appendChild(img);
  cardContent.appendChild(poetName);
  cardContent.appendChild(poetBio);
  cardContent.appendChild(button);

  cardBody.appendChild(cardContent);
  card.appendChild(cardBody);
  poetryWall.appendChild(card);
});


function showMore(cardId) {
  const cardText = document.getElementById(`cardText${cardId}`);
  const showMoreButton = document.getElementById(`showMoreButton${cardId}`);
  const isExpanded = cardText.classList.toggle("expanded");

  if (isExpanded) {
    showMoreButton.textContent = "Vissza";
  } else {
    cardText.classList.add("closed");
    showMoreButton.textContent = "Tovább";
  }
}
